CREATE TABLE vsfootball.user (Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Created BIGINT NOT NULL,Updated BIGINT NOT NULL, Guid VARCHAR(255) NOT NULL UNIQUE, Firstname VARCHAR(50) NOT NULL,Lastname VARCHAR(50) NOT NULL, Username VARCHAR(100) NOT NULL UNIQUE, Password VARCHAR(50),Accounttype VARCHAR(100),Accesstoken VARCHAR(255), Tokenexpiration VARCHAR(100), Verified INT, PRIMARY KEY (id));
CREATE TABLE vsfootball.feedback (Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Created BIGINT NOT NULL, Userid VARCHAR(100) NOT NULL,Comment VARCHAR(255) NOT NULL, Gameid VARCHAR(100), Screen VARCHAR(100), PRIMARY KEY (id));