CREATE TABLE vsfootball.user (Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Created BIGINT NOT NULL,Updated BIGINT NOT NULL, Guid VARCHAR(255) NOT NULL UNIQUE, Firstname VARCHAR(50),Lastname VARCHAR(50), Username VARCHAR(100) NOT NULL UNIQUE, Password VARCHAR(50),Accounttype VARCHAR(100),Accesstoken VARCHAR(255), Tokenexpiration VARCHAR(100), Verified INT,Playsowned VARCHAR(255),Platform VARCHAR(100),PRIMARY KEY (Id));
CREATE TABLE vsfootball.feedback (Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Created BIGINT NOT NULL, Userid VARCHAR(100) NOT NULL,Comment VARCHAR(255) NOT NULL, Gameid VARCHAR(100), Screen VARCHAR(100), PRIMARY KEY (Id));
CREATE TABLE vsfootball.game(Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Created BIGINT NOT NULL, Player1 VARCHAR(255) NOT NULL, Player2 VARCHAR(255) NOT NULL, Player1teamname VARCHAR(30), Player2teamname VARCHAR(30), Outcome VARCHAR(50), Player1accepteddate BIGINT , Player2accepteddate BIGINT, Enddate BIGINT, Inviteaccepted INT, PRIMARY KEY(Id) );
CREATE TABLE vsfootball.turn(Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Gameid BIGINT NOT NULL,Player1id VARCHAR(255) NOT NULL, Player2id VARCHAR(255) NOT NULL, Previousturn BIGINT, Yardline INT NOT NULL, Down INT ,Downdistance Int,Player1playselected INT , Player2playselected INT, Player1role INT NOT NULL, Player2role INT NOT NULL, Results VARCHAR(50), Playtime INT, Timeelapsedingame INT, Currentplayer1score INT NOT NULL, Currentplayer2score INT NOT NULL, PRIMARY KEY(Id));
CREATE TABLE vsfootball.play (Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Title VARCHAR(50), PRIMARY KEY (Id));
ALTER TABLE vsfootball.turn ADD CONSTRAINT fk_game_turns  FOREIGN KEY (Gameid) REFERENCES vsfootball.game(Id);  
ALTER TABLE vsfootball.feedback ADD CONSTRAINT fk_user_feedback FOREIGN KEY (Userid) REFERENCES vsfootball.user(Guid);